function setRipple(el){const dot=document.createElement("span"),largestSide=Math.sqrt(Math.pow(el.offsetWidth,2)+Math.pow(el.offsetHeight,2)),dotSize=Math.ceil(2*largestSide/100);dot.style=`\n        position: absolute;\n        left: 0;\n        top: 0;\n        width: ${dotSize}px;\n        height: ${dotSize}px;\n        z-index: 1;\n        border-radius: 50%;\n        background: #e8eaed75;\n        transfrom: translate(-50%, -50%) scale(1);\n        animation: ripple 1s ease-out forwards;\n    `,el.style.position="relative",el.style.overflow="hidden",el.addEventListener("mousedown",({pageX:pageX,pageY:pageY,currentTarget:currentTarget})=>{const x=75*(pageX-currentTarget.offsetLeft)/currentTarget.offsetWidth,y=75*(pageY-currentTarget.offsetTop)/currentTarget.offsetHeight;el.appendChild(dot),dot.style.left=x+"%",dot.style.top=y+"%",setTimeout((function(){dot.remove()}),1e3)})}document.querySelectorAll(".open_en").forEach(setRipple);
